/**
 * Create an array shifted by i steps
 * 
 * @param i
 * @param array
 * @return shifted array
 */
public static INDArray shiftArray(int i, INDArray array) {
	array.setWrapAround(true);
	INDArray slice = array.get(NDArrayIndex.interval(0 + i, array.shape()[0] + i), NDArrayIndex.all(),
			NDArrayIndex.all());
	slice = Nd4j.vstack(slice, Nd4j.zeros(i, array.shape()[1], 1));
	slice.setShape(array.shape());
	return slice;
}

